const LOAD_PROJECTS = 'project/LOAD'


const loadProjects = (projects) => ({
    type: LOAD_PROJECTS,
    projects
})



export const getProjects = () => async(dispatch) => {
    const res = await fetch(`/api/projects`);
    const projects = await res.json()
    dispatch(loadProjects(projects))
}
