const LOAD_PROJECTS = 'project/LOAD'
const ADD_PROJECTS = 'projects/ADD'


const loadProjects = (projects) => ({
    type: LOAD_PROJECTS,
    projects
})


const addOneProject = (project) => ({
    type: ADD_PROJECTS,
    projects
})



// get all projects 
export const getProjects = () => async(dispatch) => {
    const res = await fetch(`/api/projects`);
    const projects = await res.json()
    dispatch(loadProjects(projects))
}


// add a new project 
export const createOnePortfolio = (payload) => async dispatch => {
    const {
        user_id, 
        title, 
        instruction, 
        supplies,
        cost,
        duration,
        action,
        type,
        image_url,
        live_links,
        created_at,
        updated_at
    } = payload

    const res = await fetch(`/api/projects`, {
        method: 'POST',
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({user_id, title, instruction, supplies,cost, duration, action, type, image_url, live_links, created_at, updated_at})
    });

    let newProject;
    if(res.ok) {
        newProject = await res.json();
        diapatch(addOneProject(newProject))
    }

    return newProject
}



export default function projectReducer(state={}, action){
    switch (action.type) {
        case LOAD_Projects:
            const newProjects = {}
            action['projects'].projects.forEach(project => {
                newProjects[project.id] = project;
            })
            return {
                ...state,
                ...newprojects
            }
        case ADD_project:
            if(!state[action.project.id]) {
                return {
                    ...state,
                    [action.project.id] : action.project
                }
            }

            return {
                ...state,
                [action.project.id] : {
                    ...state[action.project.id],
                    ...action.watchlist
                }
            }
        default:
            return state
    }
}
