const LOAD_REVIEWS = 'review/LOAD'
const ADD_REVIEW = 'review/ADD'
const EDIT_REVIEW = 'review/EDIT'
const REMOVE_REVIEW = 'review/REMOVE'


const loadReviews = (review) => ({
    type: LOAD_REVIEWS,
    review
})

const addOneReview = (review) => ({
    type: ADD_REVIEW,
    review
})

const remove = (review) => ({
    type: REMOVE_REVIEW,
    review
})

const update = (review) => ({
    type: EDIT_REVIEW,
    review
})


// get all reviews
export const getReviews = () => async(dispatch) => {
    const res = await fetch(`/api/reviews`);
    const reviews = await res.json()
    dispatch(loadReviews(reviews))
}



// add new review 
export const createOneReview = (payload) => async dispatch => {
    const {
       project_id,
       user_id,
       body,
       created_at,
       updated_at
    } = payload 

    const res = await fetch(`/api/reviews`, {
        method: 'POST',
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({project_id, user_id, body, created_at, updated_at})
    });

    let newReview;
    if (res.ok){
        newReview = await res.json();
        dispatch(addOneReview(newReview))
    }

    return newReview
}



// edit review 
export const editReview = review => async dispatch => {
    const res = await fetch(`/api/reviews/${review.id}`, {
        method: 'PUT',
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(review)
    })

    if (res.ok) {
        const review = await res.json()
        dispatch(update(review))
    }
}


// delete a review 
export const deleteReview = reviewId => async dispatch => {
    const res = await fetch(`/api/reviews/${reviewId}`, {
        method: 'DELETE'
    })

    if (res.ok) {
        dispatch(remove(reviewId))
    }
}

export default function projectReducer(state={}, action){
    switch (action.type) {
        case LOAD_PROJECTS:
            const newProjects = {}
            action['projects'].projects.forEach(project => {
                newProjects[project.id] = project;
            })
            return {
                ...state,
                ...newprojects
            }
        case ADD_PROJECTS:
            if(!state[action.project.id]) {
                return {
                    ...state,
                    [action.project.id] : action.project
                }
            }
            return {
                ...state,
                [action.project.id] : {
                    ...state[action.project.id]
                }
            }
            case REMOVE_PORTFOLIO:
                let newState = { ...state }
                delete newState[action.portfolioId]
                return newState

            case EDIT_PROJECT:
                return {
                    ...state,
                    [action.project.id] : action.project
                }

        default:
            return state
    }
}
